mapper.update:
update dca_b_medicalaccident
file_id=#{fileId}, file_url=#{fileUrl}, user_account_name=#{userAccountName}, user_account=#{userAccount}, medical_year=#{medicalYear}, medical_result=#{medicalResult}, medical_content=#{medicalContent}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, auditMan=#{auditMan}, auditManName=#{auditManName}, auditDate=#{auditDate}, auditSuggestion=#{auditSuggestion}, IsUse=#{IsUse}, display_index=#{displayIndex},
where id=#{id}

mapper.select:
select  *  from dca_b_medicalaccident
where  1=1
     and dca_b_medicalaccident.id =#{ dcaBMedicalaccident.id} 
     and dca_b_medicalaccident.file_id like concat('%', #{ dcaBMedicalaccident.fileId} ,'%')
     and dca_b_medicalaccident.file_url like concat('%', #{ dcaBMedicalaccident.fileUrl} ,'%')
     and dca_b_medicalaccident.user_account_name like concat('%', #{ dcaBMedicalaccident.userAccountName} ,'%')
     and dca_b_medicalaccident.user_account like concat('%', #{ dcaBMedicalaccident.userAccount} ,'%')
     and dca_b_medicalaccident.medical_year like concat('%', #{ dcaBMedicalaccident.medicalYear} ,'%')
     and dca_b_medicalaccident.medical_result like concat('%', #{ dcaBMedicalaccident.medicalResult} ,'%')
     and dca_b_medicalaccident.medical_content like concat('%', #{ dcaBMedicalaccident.medicalContent} ,'%')
 and dca_b_medicalaccident.state =#{ dcaBMedicalaccident.state} 
 and dca_b_medicalaccident.IS_DELETEMARK =#{ dcaBMedicalaccident.isDeletemark} 

    AND dca_b_medicalaccident.CREATE_TIME  >=   #{dcaBMedicalaccident.createTimeFrom}


    AND dca_b_medicalaccident.CREATE_TIME  <=   #{dcaBMedicalaccident.createTimeTo}


    AND dca_b_medicalaccident.MODIFY_TIME  >=   #{dcaBMedicalaccident.modifyTimeFrom}


    AND dca_b_medicalaccident.MODIFY_TIME  <=   #{dcaBMedicalaccident.modifyTimeTo}

     and dca_b_medicalaccident.auditMan like concat('%', #{ dcaBMedicalaccident.auditMan} ,'%')
     and dca_b_medicalaccident.auditManName like concat('%', #{ dcaBMedicalaccident.auditManName} ,'%')

    AND dca_b_medicalaccident.auditDate  >=   #{dcaBMedicalaccident.auditDateFrom}


    AND dca_b_medicalaccident.auditDate  <=   #{dcaBMedicalaccident.auditDateTo}

     and dca_b_medicalaccident.auditSuggestion like concat('%', #{ dcaBMedicalaccident.auditSuggestion} ,'%')
 and dca_b_medicalaccident.display_index =#{ dcaBMedicalaccident.displayIndex}
