mapper.update:
update view_supplyplan
ebeln=#{ebeln}, ebelp=#{ebelp}, matnr=#{matnr}, txz01=#{txz01}, werkst=#{werkst}, werks=#{werks}, lgort=#{lgort}, lgortName=#{lgortName}, menge=#{menge}, meins=#{meins}, mseht=#{mseht}, netpr=#{netpr}, eindt=#{eindt}, bedat=#{bedat}, bsart=#{bsart}, G_MENGE=#{gMenge}, CHARGE=#{charge}, VFDAT=#{vfdat}, HSDAT=#{hsdat}, FPHM=#{fphm}, FPJR=#{fpjr}, FPRQ=#{fprq}, BASE_ID=#{baseId}, ID=#{id}, STATUS=#{status}, FPBM=#{fpbm}, GYSACCOUNT=#{gysaccount}, GYSNAME=#{gysname}, PKG_AMOUNT=#{pkgAmount}, PKG_NUMBER=#{pkgNumber}, UNINFORMED_STATE=#{uninformedState}, INFORMED_STATE=#{informedState}, BSART_D=#{bsartD}, LINK_PERSON=#{linkPerson}, SEND_DEPART=#{sendDepart}, LINK_TELEPHONE=#{linkTelephone}, SEND_ORDER_CODE=#{sendOrderCode}, OUT_CAUSE=#{outCause}, OUT_DATE=#{outDate}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId},
where id=#{id}

mapper.select:
SELECT COUNT(1) FROM `scm_b_supplyplan` WHERE `scm_b_supplyplan`.`IS_DELETEMARK` = 1 and `scm_b_supplyplan`.`bsart_d` = 0
AND scm_b_supplyplan.id = #{order.id}
AND scm_b_supplyplan.status = #{order.status}
AND scm_b_supplyplan.doneMenge = scm_b_supplyplan.g_Menge
AND (((scm_b_supplyplan.doneMenge is null or scm_b_supplyplan.doneMenge < scm_b_supplyplan.g_Menge) AND length(scm_b_supplyplan.SEND_ORDER_CODE) > 0) or (scm_b_supplyplan.doneMenge>0 and scm_b_supplyplan.doneMenge < scm_b_supplyplan.g_Menge))
AND scm_b_supplyplan.gysaccount = #{order.gysaccount}
AND ((scm_b_supplyplan.gysaccount = #{order.keyword_gys}) or (scm_b_supplyplan.gysname like concat('%',#{order.keyword_gys},'%')))
AND scm_b_supplyplan.base_Id = #{order.baseId}
AND (length(scm_b_supplyplan.SEND_ORDER_CODE) = 0 or scm_b_supplyplan.SEND_ORDER_CODE is null)

	   SELECT
	COUNT(1)
FROM
	(
		(
			SELECT
				base_id
			FROM
				`scm_b_supplyplan`
			WHERE
		 `scm_b_supplyplan`.`IS_DELETEMARK` = 1 and  `scm_b_supplyplan`.`bsart_d` = 0

			AND scm_b_supplyplan.id = #{order.id}


			AND scm_b_supplyplan.status = #{order.status}


			AND scm_b_supplyplan.doneMenge = scm_b_supplyplan.g_Menge


			AND (((scm_b_supplyplan.doneMenge is null or scm_b_supplyplan.doneMenge  <  scm_b_supplyplan.g_Menge) AND length(scm_b_supplyplan.SEND_ORDER_CODE)  >  0)
			 or (scm_b_supplyplan.doneMenge>0 and  scm_b_supplyplan.doneMenge  <  scm_b_supplyplan.g_Menge))


			AND scm_b_supplyplan.gysaccount = #{order.gysaccount}


			AND ((scm_b_supplyplan.gysaccount = #{order.keyword_gys}) or (scm_b_supplyplan.gysname like concat('%',#{order.keyword_gys},'%')))


			AND scm_b_supplyplan.base_Id = #{order.baseId}


			AND (length(scm_b_supplyplan.SEND_ORDER_CODE) = 0  or scm_b_supplyplan.SEND_ORDER_CODE is null)

		) a
		JOIN (
			SELECT
		scm_b_purcharseorder.id
			FROM
				`scm_b_purcharseorder`

			INNER JOIN scm_b_userandarea A2 ON CONCAT(scm_b_purcharseorder.werks,scm_b_purcharseorder.lgort)  = A2.AreaID  AND A2.UserID=${order.ebelp}

		WHERE
		1 = 1

			AND scm_b_purcharseorder.ebeln = #{order.ebeln}


			AND scm_b_purcharseorder.werks = #{order.werks}


			AND scm_b_purcharseorder.lgort = #{order.lgort}


			AND scm_b_purcharseorder.gysname like concat('%',#{order.gysname},'%')


			AND scm_b_purcharseorder.txz01 like concat('%',#{order.txz01},'%')


			AND scm_b_purcharseorder.matnr = #{order.matnr}


			AND ((scm_b_purcharseorder.matnr = #{order.keyword_mater}) or (scm_b_purcharseorder.txz01 like concat('%',#{order.keyword_mater},'%')))



			AND scm_b_purcharseorder.bedat  >=   #{order.eindt}


			AND scm_b_purcharseorder.bedat  <=  #{order.bedat}


		) b
	)
WHERE
	(a.`BASE_ID` = b.`ID`)

SELECT b.`ebeln` AS `ebeln`, b.`ebelp` AS `ebelp`, b.`matnr` AS `matnr`, b.`txz01` AS `txz01`, b.`werkst` AS `werkst`, b.`werks` AS `werks`, b.`lgort` AS `lgort`, b.`lgortName` AS `lgortName`, b.`menge` AS `menge`, b.`meins` AS `meins`, b.`mseht` AS `mseht`, b.`netpr` AS `netpr`, b.`eindt` AS `eindt`, b.`bedat` AS `bedat`, b.`bsart` AS `bsart`, a.`CHARGE` AS `CHARGE`, a.`VFDAT` AS `VFDAT`, a.`HSDAT` AS `HSDAT`, a.`FPHM` AS `FPHM`, a.`FPJR` AS `FPJR`, a.`FPRQ` AS `FPRQ`, a.`BASE_ID` AS `BASE_ID`, a.`ID` AS `ID`, a.`STATUS` AS `STATUS`, (CASE WHEN a.`STATUS`=1 then a.g_Menge ELSE a.DoneMenge end) AS `DONEMENGE`, a.`FPBM` AS `FPBM`, a.`GYSACCOUNT` AS `GYSACCOUNT`, a.`GYSNAME` AS `GYSNAME`, a.`G_Menge` as `GMenge`, a.`PKG_AMOUNT` AS `PKG_AMOUNT`, a.`PKG_NUMBER` AS `PKG_NUMBER`, a.`UNINFORMED_STATE` AS `UNINFORMED_STATE`, a.`INFORMED_STATE` AS `INFORMED_STATE`, a.`BSART_D` AS `BSART_D`, a.`LINK_PERSON` AS `LINK_PERSON`, a.`SEND_DEPART` AS `SEND_DEPART`, a.`LINK_TELEPHONE` AS `LINK_TELEPHONE`, a.`SEND_ORDER_CODE` AS `SEND_ORDER_CODE`, a.`OUT_CAUSE` AS `OUT_CAUSE`, a.`OUT_DATE` AS `OUT_DATE`, a.`IS_DELETEMARK` AS `IS_DELETEMARK`, a.`CREATE_TIME` AS `CREATE_TIME`, a.`MODIFY_TIME` AS `MODIFY_TIME`, a.`CREATE_USER_ID` AS `CREATE_USER_ID`, a.`MODIFY_USER_ID` AS `MODIFY_USER_ID`, b.`NAME` AS `NAME` FROM ( ( SELECT * FROM `scm_b_supplyplan` WHERE `scm_b_supplyplan`.`IS_DELETEMARK` = 1 and `scm_b_supplyplan`.`bsart_d` = 0
AND scm_b_supplyplan.id = #{order.id}
AND scm_b_supplyplan.status = #{order.status}
AND scm_b_supplyplan.doneMenge = scm_b_supplyplan.g_Menge
AND (((scm_b_supplyplan.doneMenge is null or scm_b_supplyplan.doneMenge < scm_b_supplyplan.g_Menge) AND length(scm_b_supplyplan.SEND_ORDER_CODE) > 0) or (scm_b_supplyplan.doneMenge>0 and scm_b_supplyplan.doneMenge < scm_b_supplyplan.g_Menge))
AND scm_b_supplyplan.gysaccount = #{order.gysaccount}
AND ((scm_b_supplyplan.gysaccount = #{order.keyword_gys}) or (scm_b_supplyplan.gysname like concat('%',#{order.keyword_gys},'%')))
AND scm_b_supplyplan.base_Id = #{order.baseId}
AND (length(scm_b_supplyplan.SEND_ORDER_CODE) = 0 or scm_b_supplyplan.SEND_ORDER_CODE is null)
AND scm_b_supplyplan.SEND_ORDER_CODE = #{order.sendOrderCode}
AND scm_b_supplyplan.SEND_ORDER_CODE in ${order.sendCodes}
) a JOIN ( SELECT scm_b_purcharseorder.* FROM `scm_b_purcharseorder`
INNER JOIN scm_b_userandarea A2 ON CONCAT(scm_b_purcharseorder.werks,scm_b_purcharseorder.lgort) = A2.AreaID AND A2.UserID=${order.ebelp}
WHERE 1 = 1
AND scm_b_purcharseorder.ebeln = #{order.ebeln}
AND scm_b_purcharseorder.werks = #{order.werks}
AND scm_b_purcharseorder.lgort = #{order.lgort}
AND scm_b_purcharseorder.gysname like concat('%',#{order.gysname},'%')
AND scm_b_purcharseorder.txz01 like concat('%',#{order.txz01},'%')
AND scm_b_purcharseorder.matnr = #{order.matnr}
AND ((scm_b_purcharseorder.matnr = #{order.keyword_mater}) or (scm_b_purcharseorder.txz01 like concat('%',#{order.keyword_mater},'%')))
AND scm_b_purcharseorder.bedat >= #{order.eindt}
AND scm_b_purcharseorder.bedat <= #{order.bedat}
) b ) WHERE (a.`BASE_ID` = b.`ID`)

SELECT count(1) FROM ( SELECT a.base_id FROM scm_b_supplyplan a WHERE a.CREATE_TIME > DATE_SUB(curdate(), INTERVAL 6 MONTH) AND a.`status` = 1 AND a.IS_DELETEMARK = 1 AND a.bsart_d = 0 ) a INNER JOIN ( SELECT id FROM scm_b_purcharseorder b WHERE b.bedat > DATE_SUB(curdate(), INTERVAL 6 MONTH)
AND b.werks = #{order.werks}
AND b.lgort = #{order.lgort}
AND b.txz01 like concat('%',#{order.txz01},'%')
AND b.matnr = #{order.matnr}
) p WHERE a.BASE_ID = p.id

SELECT p.VFDAT, p.ID, p.CHARGE, a.werks, a.werkst, a.txz01, a.matnr, a.lgort, a.lgortName FROM ( SELECT BASE_ID, ID, VFDAT, CHARGE FROM scm_b_supplyplan WHERE CREATE_TIME > DATE_SUB( curdate( ), INTERVAL 6 MONTH ) AND `status` = 1 AND IS_DELETEMARK = 1 AND bsart_d = 0 ) p INNER JOIN scm_b_purcharseorder a ON p.BASE_ID = a.ID WHERE 1 = 1
AND a.werks = #{order.werks}
AND a.lgort = #{order.lgort}
AND a.txz01 like concat('%',#{order.txz01},'%')
AND a.matnr = #{order.matnr}
